class horaTile extends StatelessWidget {
  final String nombre;
  final String imagenUrl;
  final VoidCallback onTap;
  
  

  horaTile({
    required this.nombre,
    required this.imagenUrl,
    required this.onTap,
  });

 @override
  Widget build(BuildContext context) {
    return ListTile(
      title: Text(
        nombre,
        style: TextStyle(
          color: Color.fromARGB(255, 229, 246, 232), // Color del texto
          fontSize: 35, // Tamaño de la fuente aumentado a 20
        ),
      ),
      leading: Image.network(imagenUrl),
      onTap: onTap,
    );
  }

}

