    required this.titulo,
    required this.valor,
  });

  @override
  Widget build(BuildContext context) {
    return Padding(
      padding: const EdgeInsets.symmetric(vertical: 8.0),
      child: Row(
        mainAxisAlignment: MainAxisAlignment.spaceBetween,
        children: [
          Text(
            titulo,
            style: TextStyle(
              fontSize: 16.0,
              fontWeight: FontWeight.bold,
              color: Colors.white,
            ),
          ),
          Text(
            valor,
            style: TextStyle(
              fontSize: 20.0,
              color: Color.fromARGB(255, 164, 250, 176),
            ),
          ),
        ],
      ),
    );
  }
}

class FormularioRegistro extends StatefulWidget {
  @override
  _FormularioRegistroState createState() => _FormularioRegistroState();
}

class _FormularioRegistroState extends State<FormularioRegistro> {
  final _formKey = GlobalKey<FormState>();
  TextEditingController _nombreController = TextEditingController();
  TextEditingController _horaP = TextEditingController();
  TextEditingController _fechaController = TextEditingController();
  TextEditingController _numeroController = TextEditingController();

 @override
Widget build(BuildContext context) {
  return Scaffold(
    appBar: AppBar(
      title: Text('Registro de la terapia'),
      backgroundColor: Color.fromARGB(255, 142, 221, 145), // Color verde
    ),
      body: SingleChildScrollView(
        child: Center(
          child: Container(
            width: MediaQuery.of(context).size.width * 0.8,
            padding: const EdgeInsets.all(16.0),
            decoration: BoxDecoration(
              borderRadius: BorderRadius.circular(10.0),
              image: DecorationImage(
                image: NetworkImage(
                  'https://st2.depositphotos.com/1533202/7260/i/450/depositphotos_72602089-stock-photo-blurred-nature-background.jpg',
                ),
                fit: BoxFit.cover,
              ),
            ),
            child: Form(
              key: _formKey,
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  SizedBox(height: 150.0),
                  TextFormField(
                    controller: _nombreController,
                    decoration: InputDecoration(
                      labelText: 'Nombre',
                      labelStyle: TextStyle(color: const Color.fromARGB(255, 157, 233, 166)),
                      filled: true,
                      fillColor: Colors.black.withOpacity(0.7),
                      focusedBorder: OutlineInputBorder(
                        borderSide: BorderSide(color: Colors.white),
                        borderRadius: BorderRadius.circular(8.0),
                      ),
                    ),
                    style: TextStyle(color: Colors.white),
                    validator: (value) {
                      if (value?.isEmpty ?? true) {
                        return 'Por favor, ingresa tu nombre';
                      }
                      return null;
                    },
                  ),
                  SizedBox(height: 8.0),
                  TextFormField(
                    controller: _horaP,
                    decoration: InputDecoration(
                      labelText: 'Hora Propuesta',
                      labelStyle: TextStyle(color: const Color.fromARGB(255, 157, 233, 166)),
                      filled: true,
                      fillColor: Colors.black.withOpacity(0.7),
                      focusedBorder: OutlineInputBorder(
                        borderSide: BorderSide(color: Colors.white),
                        borderRadius: BorderRadius.circular(8.0),
                      ),
                    ),
                    style: TextStyle(color: const Color.fromARGB(255, 157, 233, 166)),
                    validator: (value) {
                      if (value?.isEmpty ?? true) {
                        return 'Por favor, ingresa la hora propuesta';
                      }
                      return null;
                    },
                  ),
                  SizedBox(height: 8.0),
                  TextFormField(
                    controller: _fechaController,
                    decoration: InputDecoration(
                      labelText: 'Fecha de la terapia',
                      labelStyle: TextStyle(color: const Color.fromARGB(255, 157, 233, 166)),
                      filled: true,
                      fillColor: Colors.black.withOpacity(0.7),
                      focusedBorder: OutlineInputBorder(
                        borderSide: BorderSide(color: Colors.white),
                        borderRadius: BorderRadius.circular(8.0),
                      ),
                    ),
                    style: TextStyle(color: const Color.fromARGB(255, 157, 233, 166)),
                    validator: (value) {
                      if (value?.isEmpty ?? true) {
                        return 'Por favor, ingresa la fecha de tu terapia';
                      }
                      return null;
                    },
                  ),
                  SizedBox(height: 8.0),
                  TextFormField(
                    controller: _numeroController,
                    decoration: InputDecoration(
                      labelText: 'Numero de telefono',
                      labelStyle: TextStyle(color: Color.fromARGB(255, 157, 233, 166)),
                      filled: true,
                      fillColor: Colors.black.withOpacity(0.7),
                      focusedBorder: OutlineInputBorder(
                        borderSide: BorderSide(color: Colors.white),
                        borderRadius: BorderRadius.circular(8.0),
                      ),
                    ),
                    style: TextStyle(color: const Color.fromARGB(255, 157, 233, 166)),
                    validator: (value) {
                      if (value?.isEmpty ?? true) {
                        return 'Por favor, ingresa tu numero de telefono';
                      }
                      return null;
                    },
                  ),
                  SizedBox(height: 16.0),
                  ElevatedButton(
                    onPressed: () {
                      _registrarTerapia();
                    },
                    child: Text('Registrar'),
                  ),
                ],
              ),
            ),
          ),
        ),
      ),
    );
  }

  void _registrarTerapia() {
    if (_formKey.currentState?.validate() ?? false) {
      String nombre = _nombreController.text;
      String hora = _horaP.text;
      String fecha = _fechaController.text;
      String numero = _numeroController.text;

      // Muestra el SnackBar
      ScaffoldMessenger.of(context).showSnackBar(
        SnackBar(
          content: Text(
              'Registro exitoso: $nombre tiene su terapia a las $hora el $fecha su contacto es $numero '),
        ),
      );

      // Navega a la pantalla RegistroExitoso
      Navigator.push(
        context,
        MaterialPageRoute(
          builder: (context) => RegistroExitoso(
            nombre: nombre,
            hora: hora,
            fecha: fecha,
            numero: numero,
          ),
        ),
      );

      // Limpia los controladores
      _nombreController.clear();
      _horaP.clear();
      _fechaController.clear();
      _numeroController.clear();
    }
  }
}

